catalog_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   var ga = new GlideAjax('x_1300284_raposa_0.Filme');
    ga.addParam('sysparm_name', 'searchFilme');
    ga.addParam('sysparm_filme', 'titanic');
    ga.getXML(callback);

    function callback(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var data = JSON.parse(answer);

	var nomeDiretor = data.Director;  

	var gaDiretor = new GlideAjax('x_1300284_raposa_0.Filme');
	gaDiretor.addParam('sysparm_name', 'criarDiretor');
	gaDiretor.addParam('nomeDiretor', nomeDiretor);
	}
	return true;

}
