catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return clearFields();
    }

    //Type appropriate comment here, and begin script below
    var ga = new GlideAjax('x_1300284_raposa_0.Filme');
    ga.addParam('sysparm_name', 'searchFilme');
    ga.addParam('sysparm_filme', newValue.replace(/ /g, '+'));
    ga.getXML(callback);

    function callback(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var data = JSON.parse(answer);
        var msgFilme = g_form.getValue('nome_do_filme');

        if (data.Response == 'False') {
            g_form.addErrorMessage("O Filme: " + msgFilme + " não existe");
            setTimeout(function() {
                g_form.clearMessages();
            }, 5000);
			clearFields();
        } else if (data.Response == 'True') {
            g_form.setValue('titulo', data.Title);
            g_form.setValue('nota_imdb', data.imdbRating);
            g_form.setValue('categoria', data.Genre);
            g_form.setValue('sinopse', data.Plot);
            g_form.setValue('poster', data.Poster);
            g_form.setValue('imagem', '<img src="' + data.Poster + '"/>');

        }
    }

    function clearFields() {
        //Clear all fields
        g_form.clearValue('nome_do_filme');
        g_form.clearValue('titulo');
        g_form.clearValue('nota_imdb');
        g_form.clearValue('categoria');
        g_form.clearValue('sinopse');
        g_form.clearValue('imagem');
    }
}
