sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

        // Add your code here
        var filme = current.variables.filme;
        var nota = current.variables.sua_nota;
		//refatorar com GlideAggregate
        var grNew = new GlideRecord('sc_req_item');
        grNew.addQuery('cat_item', '55cc75b947d6821025e7b4b4116d43bd');
        grNew.addQuery('stage', 'request_approved');
        grNew.query();

        var count = 0;
        var soma = 0;
        while (grNew.next()) {
            //var total = previous.soma + total;
            if (grNew.variables.filme == filme) {
                count = count + 1;
                soma += parseInt(grNew.variables.sua_nota);

            }
		}

            gs.info('rpv count ' + count + ' soma ' + soma);


            var gr = new GlideRecord('x_1300284_raposa_0_filmes');
            gr.addQuery('sys_id', filme);
            gr.query();

            if (gr.next()) {
                var notaAnterior = soma;
                gs.info('rpv anterioor' + notaAnterior);
                gr.nota_da_raposa = notaAnterior / count.toFixed(2);
				gs.info('rpv raposa ' + gr.nota_da_raposa);
                gr.update();
            }



        })(current, previous);
