sys_script_include.script:
var Filme = Class.create();
Filme.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

        searchFilme: function() {

            var filme = this.getParameter('sysparm_filme');

            try {
                var r = new sn_ws.RESTMessageV2('x_1300284_raposa_0.Omdb', 'Default GET');
                r.setStringParameterNoEscape('t', filme);

                var response = r.execute();
                var responseBody = response.getBody();
                var httpStatus = response.getStatusCode();

                return responseBody;


            } catch (ex) {
                var message = ex.message;
            }

        },


        type: 'Filme'
    }

);
